mapper.select:
SELECT a.*, e.EMPLOYEE_NM, e.EMPLOYEE_HD, CASE WHEN FLOOR(MONTHS_BETWEEN(SYSDATE, TO_DATE(e.EMPLOYEE_HD, 'yyyy-MM-dd')) / 12) < 1 THEN TO_CHAR(TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE(e.EMPLOYEE_HD, 'yyyy-MM-dd')))) || '개월' ELSE TO_CHAR(FLOOR(MONTHS_BETWEEN(SYSDATE, TO_DATE(e.EMPLOYEE_HD, 'yyyy-MM-dd')) / 12)) || '년' END AS service_years FROM ANNUAL a INNER JOIN EMPLOYEE e ON a.EMPLOYEE_CD = e.EMPLOYEE_CD
a.EMPLOYEE_CD = #{employee_cd}
e.employee_nm = #{employee_nm}
AND e.EMPLOYEE_DP = #{employee_dp}
AND #{employee_hd1} <= e.EMPLOYEE_HD AND e.EMPLOYEE_HD <= #{employee_hd2}
AND a.ANNUAL_RA = #{annual_ra}

mapper.insert:
INSERT INTO ANNUAL (EMPLOYEE_CD
