				    , ANNUAL_RU
				    , ANNUAL_RD
				    , ANNUAL_UU
				    , ANNUAL_UD
					)
			VALUES (#{EMPLOYEE_CD}
		        	, #{ANNUAL_YR} + 1
					, 0 
					, #{EXPECT_ANNUAL} 
					, #{EXPECT_ANNUAL} 
					, #{ANNUAL_RU} 
					, #{ANNUAL_RD} 
					, NULL
					, NULL)
