mapper.select:
SELECT a.APPROVAL_TT , a.APPROVAL_WR , c.COMMON_NM AS APPROVAL_AV , TO_CHAR(TO_DATE(JSON_VALUE(a.APPROVAL_CT, '$.startday'), 'YYYY-MM-DD'), 'YYYY-MM-DD') AS STARTDAY , TO_CHAR(TO_DATE(JSON_VALUE(a.APPROVAL_CT, '$.endday'), 'YYYY-MM-DD'), 'YYYY-MM-DD') AS ENDDAY , TO_CHAR(JSON_VALUE(a.APPROVAL_CT, '$.reason')) AS REASON , e.EMPLOYEE_NM , (SELECT COUNT(*) FROM APPROVAL WHERE 1=1 AND APPROVAL_FA = #{currentCd} AND APPROVAL_WR = a.APPROVAL_WR) AS APPROVAL_COUNT FROM APPROVAL a LEFT JOIN employee e ON e.employee_id = a.APPROVAL_WR LEFT JOIN COMMON_CODE c ON a.APPROVAL_AV = c.COMMON_CC AND c.COMMON_GC = 'APPROVAL' WHERE 1=1
AND e.EMPLOYEE_CD = #{currentCd}
AND a.APPROVAL_FA = #{currentCd}
FETCH FIRST 4 ROWS ONLY
