mapper.select:
SELECT * FROM COMMON_CODE
WHERE COMMON_GC = '00'
WHERE COMMON_GC = #{code}

mapper.insert:
INSERT ALL

    INTO COMMON_CODE (
        COMMON_GC
        , COMMON_CC
        , COMMON_NM
        , COMMON_CT
        , COMMON_US
        , COMMON_IN
        , COMMON_RU
        , COMMON_RD
    ) VALUES (
        #{code.COMMON_GC}
        , #{code.COMMON_CC}
        , #{code.COMMON_NM}
        , #{code.COMMON_CT}
        , #{code.COMMON_US}
        , #{code.COMMON_IN}
        , #{code.COMMON_RU}
        , #{code.COMMON_RD}
    )

SELECT * FROM dual
