mapper.insert:
INSERT INTO COMMON_CODE (
    , COMMON_GC
    , COMMON_CC
    , COMMON_NM
    , COMMON_CT
    , COMMON_US
    , COMMON_IN
    , COMMON_RU
    , COMMON_RD
) VALUES (
    , #{code.common_gc}
    , #{code.common_cc}
    , #{code.common_nm}
    , #{code.common_ct}
    , #{code.common_us}
    , #{code.common_in}
    , #{code.common_ru}
    , #{code.common_rd}
)

INSERT ALL

    INTO COMMON_CODE (
        , COMMON_GC
        , COMMON_CC
        , COMMON_NM
        , COMMON_CT
        , COMMON_US
        , COMMON_IN
        , COMMON_RU
        , COMMON_RD
    ) VALUES (
        , #{code.common_gc}
        , #{code.common_cc}
        , #{code.common_nm}
        , #{code.common_ct}
        , #{code.common_us}
        , #{code.common_in}
        , #{code.common_ru}
        , #{code.common_rd}
    )

SELECT * FROM dual

mapper.update:
	UPDATE COMMON_CODE SET
          COMMON_CC = #{code.common_cc}
        , COMMON_NM = #{code.common_nm}
        , COMMON_CT = #{code.common_ct}
        , COMMON_US = #{code.common_us}
        , COMMON_IN = #{code.common_in}
        , COMMON_UU = #{code.common_uu}
        , COMMON_UD = #{code.common_ud}
	WHERE COMMON_GC = #{code.common_gc}
	  AND COMMON_CC = #{code.beforeCommon_cc}

mapper.select:
SELECT COUNT(*)
FROM COMMON_CODE
WHERE COMMON_GC = #{code}

mapper.update:
UPDATE COMMON_CODE SET
	COMMON_GC = #{after}
WHERE COMMON_GC = #{before}
