mapper.select:
WITH PRODUCT_DATA AS ( SELECT PD.PRODUCT_GC ITEM_GC, CC2.COMMON_NM ITEM_GN, PD.PRODUCT_CC ITEM_CC, CC3.COMMON_NM ITEM_CN, PD.PRODUCT_NM ITEM_NM, WH.WAREHOUSE_NM, WA.WAREAREA_NM, CC.COMMON_NM COLOR_NM, ST.WAREHOUSE_CD, ST.WAREAREA_CD, ST.STOCK_AQ, ST.STOCK_TP, PD.PRODUCT_UN ITEM_UN, SUBSTR(ST.ITEM_CD, 1, INSTR(ST.ITEM_CD, '-') - 1) ITEM_CD, SUBSTR(ST.ITEM_CD, INSTR(ST.ITEM_CD, '-', -1) + 1) COLOR, SUBSTR(ST.ITEM_CD, INSTR(ST.ITEM_CD, '-') + 1, 3) "SIZE", WA.WAREAREA_CP - SUM(ST.STOCK_AQ) OVER (PARTITION BY ST.WAREHOUSE_CD, ST.WAREAREA_CD) WAREAREA_CP FROM STOCK ST LEFT OUTER JOIN PRODUCT PD ON PD.PRODUCT_CD = SUBSTR(ST.ITEM_CD, 1, INSTR(ST.ITEM_CD, '-') - 1) AND PD.PRODUCT_CR = SUBSTR(ST.ITEM_CD, INSTR(ST.ITEM_CD, '-', -1) + 1) AND PD.PRODUCT_SZ = SUBSTR(ST.ITEM_CD, INSTR(ST.ITEM_CD, '-') + 1, 3) LEFT OUTER JOIN WAREHOUSE WH ON WH.WAREHOUSE_CD = ST.WAREHOUSE_CD LEFT OUTER JOIN WAREAREA WA ON WA.WAREAREA_CD = ST.WAREAREA_CD AND WA.WAREHOUSE_CD = ST.WAREHOUSE_CD LEFT OUTER JOIN COMMON_CODE CC ON CC.COMMON_GC = 'COLOR' AND CC.COMMON_CC = SUBSTR(ST.ITEM_CD, INSTR(ST.ITEM_CD, '-', -1) + 1) LEFT OUTER JOIN COMMON_CODE CC2 ON CC2.COMMON_GC = 'PRDTYPE' AND CC2.COMMON_CC = PD.PRODUCT_GC LEFT OUTER JOIN COMMON_CODE CC3 ON CC3.COMMON_GC = PD.PRODUCT_GC AND CC3.COMMON_CC = PD.PRODUCT_CC WHERE ST.STOCK_TP = 'Y' AND ST.WAREHOUSE_CD = #{filter.warehouse_cd} AND ST.WAREAREA_CD = #{filter.warearea_cd} AND PD.PRODUCT_GC = #{filter.item_gc} AND PD.PRODUCT_CC = #{filter.item_cc} AND PD.PRODUCT_NM LIKE '%${filter.item_nm}%' ), PIVOT_DATA AS ( SELECT * FROM PRODUCT_DATA PIVOT ( SUM(STOCK_AQ) FOR "SIZE" IN
'${size.common_cc}' AS "${size.common_cc}"
) ), UNION_DATA AS ( SELECT PVT.ITEM_GC, PVT.ITEM_GN, PVT.ITEM_CC, PVT.ITEM_CN, PVT.ITEM_NM, PVT.ITEM_CD, PVT.COLOR, PVT.ITEM_UN, PVT.WAREHOUSE_NM, PVT.WAREHOUSE_CD, PVT.WAREAREA_NM, PVT.WAREAREA_CD, PVT.COLOR_NM, PVT.WAREAREA_CP, PVT.STOCK_TP,
NVL(PVT."${size.common_cc}", 0) AS SIZE_${size.common_cc} , (
NVL(PVT."${size.common_cc}", 0)
) STOCK_AQ FROM PIVOT_DATA PVT UNION ALL SELECT ST.ITEM_GC, ST.ITEM_GN, ST.ITEM_CC, ST.ITEM_CN, ST.ITEM_NM, ST.ITEM_CD, NULL AS COLOR, ST.ITEM_UN, ST.WAREHOUSE_NM, ST.WAREHOUSE_CD, ST.WAREAREA_NM, ST.WAREAREA_CD, NULL AS COLOR_NM, ST.WAREAREA_CP, ST.STOCK_TP,
0 AS SIZE_${size.common_cc} , ST.STOCK_AQ FROM ( SELECT MR.MATERIAL_GC ITEM_GC, CC.COMMON_NM ITEM_GN, MR.MATERIAL_CC ITEM_CC, CC2.COMMON_NM ITEM_CN, MR.MATERIAL_NM ITEM_NM, MR.MATERIAL_CD ITEM_CD, WH.WAREHOUSE_NM, WH.WAREHOUSE_CD, WA.WAREAREA_NM, WA.WAREAREA_CD, MR.MATERIAL_UN ITEM_UN, ST.STOCK_TP, ST.STOCK_AQ, WA.WAREAREA_CP - SUM(ST.STOCK_AQ) OVER (PARTITION BY ST.WAREHOUSE_CD, ST.WAREAREA_CD) WAREAREA_CP FROM STOCK ST LEFT OUTER JOIN MATERIAL MR ON MR.MATERIAL_CD = ST.ITEM_CD LEFT OUTER JOIN WAREHOUSE WH ON WH.WAREHOUSE_CD = ST.WAREHOUSE_CD LEFT OUTER JOIN WAREAREA WA ON WA.WAREAREA_CD = ST.WAREAREA_CD AND WA.WAREHOUSE_CD = ST.WAREHOUSE_CD LEFT OUTER JOIN COMMON_CODE CC ON CC.COMMON_GC = 'PRDTYPE' AND CC.COMMON_CC = MR.MATERIAL_GC LEFT OUTER JOIN COMMON_CODE CC2 ON CC2.COMMON_GC = MR.MATERIAL_GC AND CC2.COMMON_CC = MR.MATERIAL_CC WHERE ST.STOCK_TP = 'N' AND ST.WAREHOUSE_CD = #{filter.warehouse_cd} AND ST.WAREAREA_CD = #{filter.warearea_cd} AND MR.MATERIAL_GC = #{filter.item_gc} AND MR.MATERIAL_CC = #{filter.item_cc} AND MR.MATERIAL_NM LIKE '%${filter.item_nm}%' ) ST ) SELECT ST.ITEM_GC, ST.ITEM_GN, ST.ITEM_CC, ST.ITEM_CN, ST.ITEM_NM, ST.ITEM_CD, ST.COLOR, ST.ITEM_UN, ST.WAREHOUSE_NM, ST.WAREHOUSE_CD, ST.WAREAREA_NM, ST.WAREAREA_CD, ST.COLOR_NM, ST.WAREAREA_CP, ST.STOCK_TP, ST.STOCK_AQ,
ST.SIZE_${size.common_cc}
FROM ( SELECT UD.*, CASE WHEN ITEM_GC = 'MATERIALS' THEN 1 WHEN ITEM_GC = 'HALFPRO' THEN 2 WHEN ITEM_GC = 'PRODUCT' THEN 3 ELSE 4 END SORT_ORDER FROM UNION_DATA UD ) ST ORDER BY ST.WAREHOUSE_CD, ST.WAREAREA_CD, ST.SORT_ORDER, ST.ITEM_CD, ST.COLOR

SELECT ST.STOCK_AQ, WS.WAREAREA_CP
  FROM STOCK ST
  LEFT OUTER JOIN (
  	SELECT WA.WAREHOUSE_CD, WA.WAREAREA_CD, NVL(WA.WAREAREA_CP - STOCK_MQ, WA.WAREAREA_CP) WAREAREA_CP
  	  FROM WAREAREA WA
  	  LEFT OUTER JOIN (
	  	  SELECT SUM(ST.STOCK_AQ) STOCK_MQ, ST.WAREHOUSE_CD, ST.WAREAREA_CD 
		    FROM STOCK ST
		   GROUP BY ST.WAREHOUSE_CD, ST.WAREAREA_CD 
  	  ) ST ON ST.WAREHOUSE_CD = WA.WAREHOUSE_CD AND ST.WAREAREA_CD = WA.WAREAREA_CD
  ) WS ON WS.WAREHOUSE_CD = ST.WAREHOUSE_CD AND WS.WAREAREA_CD = ST.WAREAREA_CD
WHERE ST.ITEM_CD = #{stock.item_cd}
  AND ST.WAREHOUSE_CD  = #{stock.warehouse_cd}
  AND ST.WAREAREA_CD = #{stock.warearea_cd}
